import axios from "axios";
import React, { useState } from "react";

function FormularioCadastro() {
    const [nome, setNome] = useState("");
    const [estacao, setEstacao] = useState("");
    const [periodo, setPeriodo] = useState("");
    const [data, setData] = useState("");

    const dados = {
        nome: nome,
        estacao: estacao,
        periodo: periodo,
        data: data
    }

    function submitForm(e) {
        e.preventDefault();
        axios.post("http://localhost:8000/api/v1/reservas/", dados)
    }


  return (
    <div className="bg-white rounded-lg max-w-sm flex items-center justify-center shadow-md mx-auto ">
      <form className="max-w-md mx-auto p-5">
        <div className="mb-4">
          <label htmlFor="nome" className="block mb-2 font-medium">
            Nome:
          </label>
          <input
            type="text"
            id="nome"
            value={nome}
            onChange={(e) => setNome(e.nome)}
            className="border border-gray-300 rounded-md py-2 px-4 w-full focus:outline-none focus:ring focus:border-blue-300"
          />
        </div>

        <div className="mb-4">
          <label htmlFor="estacao" className="block mb-2 font-medium">
            Estação:
          </label>
          <select
            id="estacao"
            value={estacao}
            onChange={(e) => setEstacao(e.estacao)}
            className="border border-gray-300 rounded-md py-2 px-4 w-full focus:outline-none focus:ring focus:border-blue-300"
          >
            <option value="">Selecione uma estação</option>
            <option value="Primavera">Primavera</option>
            <option value="Verão">Verão</option>
            <option value="Outono">Outono</option>
            <option value="Inverno">Inverno</option>
          </select>
        </div>

        <div className="mb-4">
          <label htmlFor="data" className="block mb-2 font-medium">
            Data:
          </label>
          <input
            type="date"
            id="data"
            value={data}
            onChange={(e) => setData(e.Data)}
            className="border border-gray-300 rounded-md py-2 px-4 w-full focus:outline-none focus:ring focus:border-blue-300"
          />
        </div>

        <button
          type="submit"
          onClick={submitForm}
          className="bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600 focus:outline-none focus:ring focus:border-blue-300"
        >
          Enviar
        </button>
      </form>
    </div>
  );
};

export default FormularioCadastro;
