import React from "react";
import axios from "axios";
import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

const Edit = () => {
    
    const InitialState = { id: 0, nome: "", periodo: "", estacao: "", data: "" };
    const { id } = useParams();
    const [dados, setDados] = useState(InitialState);
    const [options, setOptions] = useState([]);
    const navigate = useNavigate();

    const handleInputChange = (e) => {
        setDados({ ...dados, [e.target.id]: e.target.value });
      };

      useEffect(() => {
        loadData();
      }, []);
    
      const handleUpdate = async (e) => {
        e.preventDefault();
        await axios.put(`http://localhost:8080/user/${id}`, dados);
        navigate("/");
      };
    
      const loadData = async () => {
        const result = await axios.get(`http://localhost:8080/user/${id}`);
        setDados(result.data);
      };


    const fetchData = async () => {
        try {
          const response = await axios.get("http://localhost:8000/api/v1/estacao/");
          setOptions(response.data);
        } catch (error) {
          console.error(error);
        }
      };
    
      useEffect(() => {
        fetchData();
      }, []);
        
    
  return (
    <div className="bg-transparent rounded-lg flex items-center justify-center ">
      <form onSubmit={handleUpdate} className="max-w-md mx-auto p-5" >
        <div className="mb-4 text-center">
          <label
            htmlFor="nome"
            className="block mb-4 text-xl text-white font-semibold"
          >
            Nome:
          </label>
          <input
            type="text"
            id="nome"
            value={dados.nome}
            onChange={handleInputChange}
            className="border border-gray-300 rounded-md py-2 px-4 w-full focus:outline-none focus:ring focus:border-blue-300"
            placeholder="Digite seu nome"
          />
        </div>

        <div className="mb-4 text-center">
          <label
            htmlFor="estacao"
            className="block mb-4 text-xl text-white font-semibold"
          >
            Esta√ß√£o:
          </label>
          <select
            id="estacao"
            value={dados.estacao}
            onChange={handleInputChange}
            className="border border-gray-300 rounded-md py-2 px-4 w-full focus:outline-none focus:ring focus:border-blue-300"
          >
            <option value="">Selecione uma esta√ß√£o</option>
            {options.map((option) => (
              <option key={option.id} value={option.nome}>
                {option.nome}
              </option>
            ))}
          </select>
        </div>

        <div className="mb-4 text-center">
          <label className="block mb-4 text-xl text-white font-semibold">
            Per√≠odo:
          </label>
          <div className="mb-4 flex">
            <div>
              <label className="flex items-center pr-3">
                <input
                  type="radio"
                  id="periodo"
                  value="Manh√£"
                  checked={dados.periodo === "Manh√£"}
                  onChange={handleInputChange}
                  className="form-radio h-4 w-4 text-blue-500 focus:ring focus:border-blue-300"
                />
                <span className="ml-2 text-white font-semibold">Manh√£</span>
              </label>
            </div>
            <div>
              <label className="flex items-center pr-3">
                <input
                  type="radio"
                  id="periodo"
                  value="Tarde"
                  checked={dados.periodo === "Tarde"}
                  onChange={handleInputChange}
                  className="form-radio h-4 w-4 text-blue-500 focus:ring focus:border-blue-300"
                />
                <span className="ml-2 text-white font-semibold">Tarde</span>
              </label>
            </div>
            <div>
              <label className="flex items-center">
                <input
                  type="radio"
                  id="periodo"
                  value="Noite"
                  checked={dados.periodo === "Noite"}
                  onChange={handleInputChange}
                  className="form-radio h-4 w-4 text-blue-500 focus:ring focus:border-blue-300"
                />
                <span className="ml-2 text-white font-semibold">Noite</span>
              </label>
            </div>
          </div>
        </div>

        <div className="mb-4 text-center">
          <label className="block mb-4 text-xl text-white font-semibold">
            Data:
          </label>
          <input
            type="date"
            id="data"
            value={dados.data}
            onChange={handleInputChange}
            className="border border-gray-300 rounded-md py-2 px-4 w-full focus:outline-none focus:ring focus:border-blue-300"
          />
        </div>
        <div className="flex justify-center">
          <button
            type="submit"
            className=" hover:bg-white w-52 text-slate-50 mt-4 font-semibold hover:text-black py-2 px-4 border border-blue-500 hover:border-transparent rounded"
          >
            Enviar
          </button>
        </div>
      </form>
    </div>
  );
}

export default Edit;
