import React from "react";
import axios from "axios";
import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";


const Edit = () => {
    const { id } = useParams();
    const [dados, setDados] = useState([]);
    const [options, setOptions] = useState([]);
    let navigate = useNavigate();
    
      const { nome, estacao, periodo, data } = dados;
    
      const onInputChange = (e) => {
        setDados({ ...dados, [e.target.name]: e.target.value });
      };
    
      useEffect(() => {
        loadDados();
      }, []);
    
      const handleUpdate = async (e) => {
        e.preventDefault();
        await axios.put(`http://localhost:8080/user/${id}`, dados);
        navigate("/reservas");
      };
    
      const loadDados = async () => {
        const result = await axios.get(`http://localhost:8080/user/${id}`);
        setDados(result.data);
      };


    const fetchData = async () => {
        try {
          const response = await axios.get("http://localhost:8000/api/v1/estacao/");
          setOptions(response.data);
        } catch (error) {
          console.error(error);
        }
      };
    
      useEffect(() => {
        fetchData();
      }, []);
        
    
  return (
    <div className="bg-transparent rounded-lg flex items-center justify-center ">
      <form onSubmit={(e) => handleUpdate(e)} className="max-w-md mx-auto p-5" >
        <div className="mb-4 text-center">
          <label
            htmlFor="nome"
            className="block mb-4 text-xl text-white font-semibold"
          >
            Nome:
          </label>
          <input
            type="text"
            id="nome"
            value={nome}
            onChange={(e) => onInputChange(e)}
            className="border border-gray-300 rounded-md py-2 px-4 w-full focus:outline-none focus:ring focus:border-blue-300"
            placeholder="Digite seu nome"
          />
        </div>

        <div className="mb-4 text-center">
          <label
            htmlFor="estacao"
            className="block mb-4 text-xl text-white font-semibold"
          >
            EstaÃ§Ã£o:
          </label>
          <select
            id="estacao"
            value={estacao}
            onChange={(e) => onInputChange(e)}
            className="border border-gray-300 rounded-md py-2 px-4 w-full focus:outline-none focus:ring focus:border-blue-300"
          >
            <option value="">Selecione uma estaÃ§Ã£o</option>
            {options.map((option) => (
              <option key={option.id} value={option.nome}>
                {option.nome}
              </option>
            ))}
          </select>
        </div>

        <div className="mb-4 text-center">
          <label className="block mb-4 text-xl text-white font-semibold">
            PerÃ­odo:
          </label>
          <div className="mb-4 flex">
            <div>
              <label className="flex items-center pr-3">
                <input
                  type="radio"
                  id="periodo"
                  value={periodo}
                  checked={periodo === "ManhÃ£"}
                  onChange={(e) => onInputChange(e)}
                  className="form-radio h-4 w-4 text-blue-500 focus:ring focus:border-blue-300"
                />
                <span className="ml-2 text-white font-semibold">ManhÃ£</span>
              </label>
            </div>
            <div>
              <label className="flex items-center pr-3">
                <input
                  type="radio"
                  id="periodo"
                  value="Tarde"
                  checked={periodo === "Tarde"}
                  onChange={(e) => onInputChange(e)}
                  className="form-radio h-4 w-4 text-blue-500 focus:ring focus:border-blue-300"
                />
                <span className="ml-2 text-white font-semibold">Tarde</span>
              </label>
            </div>
            <div>
              <label className="flex items-center">
                <input
                  type="radio"
                  id="periodo"
                  value="Noite"
                  checked={periodo === "Noite"}
                  onChange={(e) => onInputChange(e)}
                  className="form-radio h-4 w-4 text-blue-500 focus:ring focus:border-blue-300"
                />
                <span className="ml-2 text-white font-semibold">Noite</span>
              </label>
            </div>
          </div>
        </div>

        <div className="mb-4 text-center">
          <label className="block mb-4 text-xl text-white font-semibold">
            Data:
          </label>
          <input
            type="date"
            id="data"
            value={data}
            onChange={(e) => onInputChange(e)}
            className="border border-gray-300 rounded-md py-2 px-4 w-full focus:outline-none focus:ring focus:border-blue-300"
          />
        </div>
        <div className="flex justify-center">
          <button
            type="submit"
            className=" hover:bg-white w-52 text-slate-50 mt-4 font-semibold hover:text-black py-2 px-4 border border-blue-500 hover:border-transparent rounded"
          >
            Enviar
          </button>
        </div>
      </form>
    </div>
  );
}

export default Edit;
