import React, { useState } from 'react';

const FormularioCadastro = () => {
  const [nome, setNome] = useState('');
  const [estacao, setEstacao] = useState('');
  const [periodo, setPeriodo] = useState('');
  const [data, setData] = useState('');

  const handleChangeNome = (event) => {
    setNome(event.target.value);
  };

  const handleChangeEstacao = (event) => {
    setEstacao(event.target.value);
  };

  const handleChangePeriodo = (event) => {
    setPeriodo(event.target.value);
  };

  const handleChangeData = (event) => {
    setData(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Aqui você pode fazer algo com os dados do formulário, como enviar para uma API ou realizar alguma ação no frontend
    console.log('Nome:', nome);
    console.log('Estação:', estacao);
    console.log('Período:', periodo);
    console.log('Data:', data);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label htmlFor="nome">Nome:</label>
      <input type="text" id="nome" value={nome} onChange={handleChangeNome} />

      <label htmlFor="estacao">Estação:</label>
      <select id="estacao" value={estacao} onChange={handleChangeEstacao}>
        <option value="">Selecione uma estação</option>
        <option value="Primavera">Primavera</option>
        <option value="Verão">Verão</option>
        <option value="Outono">Outono</option>
        <option value="Inverno">Inverno</option>
      </select>

      <label>Período:</label>
      <div>
        <label htmlFor="manha">
          <input
            type="radio"
            id="manha"
            value="Manhã"
            checked={periodo === 'Manhã'}
            onChange={handleChangePeriodo}
          />
          Manhã
        </label>
      </div>
      <div>
        <label htmlFor="tarde">
          <input
            type="radio"
            id="tarde"
            value="Tarde"
            checked={periodo === 'Tarde'}
            onChange={handleChangePeriodo}
          />
          Tarde
        </label>
      </div>
      <div>
        <label htmlFor="noite">
          <input
            type="radio"
            id="noite"
            value="Noite"
            checked={periodo === 'Noite'}
            onChange={handleChangePeriodo}
          />
          Noite
        </label>
      </div>

      <label htmlFor="data">Data:</label>
      <input type="date" id="data" value={data} onChange={handleChangeData} />

      <button type="submit">Enviar</button>
    </form>
  );
};

export default FormularioCadastro;
