const URL = "http://localhost:8000/api/v1/reservas/"


export const novaReserva = async (newReserva) => {
    return await fetch(URL, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            "nome": String(newReserva.nome).trim(),
            "estacao": String(newReserva.estacao).trim(),
            "periodo": String(newReserva.periodo).trim(),
            "data": String(newReserva.data).trim(),

        })
    });
};

export const deleteReserva = async (id) => {
    const headers = new Headers();
    headers.append('Content-Type', 'application/json');
    headers.append('Accept', 'application/json');
    return await fetch(`${URL}${id}`, {
        method: 'DELETE',
        headers: headers
    });
}
