import React, { useState, useEffect } from "react";
import * as requests from "./requests";
import axios from "axios";

function Form() {
  const InitialState = { id: 0, nome: "", periodo: "", estacao: "", data: "" };

  const [dados, setDados] = useState(InitialState);
  const [options, setOptions] = useState([]);
  const [errors, setErrors] = useState({});

  

  const handleInputChange = (e) => {
    setDados({ ...dados, [e.target.id]: e.target.value });   
  };

  const validateForm = () => {
    let errors = {};

    // Check if required fields are empty
    if (!dados.nome) {
      errors.nome = "Campo obrigatório";
    }

    if (!dados.estacao) {
      errors.estacao = "Campo obrigatório";
    }

    if (!dados.periodo) {
      errors.periodo = "Campo obrigatório";
    }

    if (!dados.data) {
      errors.data = "Campo obrigatório";
    }

    setErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validateForm()) {
      try {
        let res;
        res = await requests.novaReserva(dados);
        const data = await res.json();
        console.log(data);
        setDados(InitialState);
      } catch {
        console.log("erro");
      }
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get("http://localhost:8000/api/v1/estacao/");
      setOptions(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="bg-transparent rounded-lg max-w-sm flex items-center justify-center mx-auto ">
      <form className="max-w-md mx-auto p-5" onSubmit={handleSubmit}>
        <div className="mb-4 text-center">
          <label htmlFor="nome" className="block mb-2 font-medium">
            Nome:
          </label>
          {errors.nome && <p className="text-red-500">{errors.nome}</p>}
          <input
            type="text"
            id="nome"
            value={dados.nome}
            onChange={handleInputChange}
            className="border w-full border-gray-300 rounded-md py-2 px-4 w-full focus:outline-none focus:ring focus:border-blue-300"
            placeholder="Digite seu nome"
          />
        </div>

        <div className="mb-4 text-center">
          <label htmlFor="estacao" className="block mb-2 font-medium">
            Estação:
          </label>
          {errors.estacao && <p className="text-red-500">{errors.estacao}</p>}
          <select
            id="estacao"
            value={dados.estacao}
            onChange={handleInputChange}
            className="border border-gray-300 rounded-md py-2 px-4 w-full focus:outline-none focus:ring focus:border-blue-300"
          >
            <option value="">Selecione uma estação</option>
            {options.map((option) => (
              <option key={option.id} value={option.nome}>
                {option.nome}
              </option>
            ))}
          </select>
        </div>

        <div className="mb-4 text-center">
          <label className="block mb-2 font-medium">
            Período:
            </label>
            {errors.periodo && <p className="text-red-500">{errors.perido}</p>}
          <div className="mb-4 flex">
            <div>
              <label htmlFor="manha" className="flex items-center pr-3">
                <input
                  type="radio"
                  id="periodo"
                  value={"Manhã"}
                  checked={dados.periodo === "Manhã"}
                  onChange={handleInputChange}
                  className="form-radio h-4 w-4 text-blue-500 focus:ring focus:border-blue-300"
                />
                <span className="ml-2">Manhã</span>
              </label>
            </div>
            <div>
              <label htmlFor="tarde" className="flex items-center pr-3">
                <input
                  type="radio"
                  id="periodo"
                  value="Tarde"
                  checked={dados.periodo === "Tarde"}
                  onChange={handleInputChange}
                  className="form-radio h-4 w-4 text-blue-500 focus:ring focus:border-blue-300"
                />
                <span className="ml-2">Tarde</span>
              </label>
            </div>
            <div>
              <label htmlFor="noite" className="flex items-center">
                <input
                  type="radio"
                  id="periodo"
                  value="Noite"
                  checked={dados.periodo === "Noite"}
                  onChange={handleInputChange}
                  className="form-radio h-4 w-4 text-blue-500 focus:ring focus:border-blue-300"
                />
                <span className="ml-2">Noite</span>
              </label>
            </div>
          </div>
        </div>

        <div className="mb-4 text-center">
          <label htmlFor="data" className="block mb-2 font-medium">
            Data:
          </label>
          {errors.data && <p className="text-red-500">{errors.data}</p>}
          <input
            type="date"
            id="data"
            value={dados.data}
            onChange={handleInputChange}
            className="border border-gray-300 rounded-md py-2 px-4 w-full focus:outline-none focus:ring focus:border-blue-300"
          />
        </div>
        <div className="flex justify-center">
        <button
          type="submit"
          className="bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600 focus:outline-none focus:ring focus:border-blue-300">
          Enviar
        </button>
        </div>
      </form>
    </div>
  );
}

export default Form;
